import React from 'react'
const img = "https://image.tmdb.org/t/p/w500";

const Content = ({searched, genres}) => {
  return(
    <> 
    <div>
{genres.map((genre)=>
<p key={genre.id}>{genre}  </p>
)}
    </div>
  <div>
{searched.map((movies)=>
  <img src={`${img}${movies.poster_path}`} alt='movies'/>
)}
  </div>
  </>
  )
}

export default Content
