import axios from 'axios';
import React, { useState } from 'react'
import Content from './Content';


const Header = ({genres}) => {
  const [searchMovie, setSearchMovie] = useState([])
 const handleSearch=(e)=>{
  e.preventDefault();
  axios.get(`https://api.themoviedb.org/3/search/movie?api_key=3e9bf10c4b9da58346377bed9363e958&query=${e.target.value}`)
  .then(movie => movie.data)
  .then(res=> setSearchMovie(res.results))
 } 
  return (
    <>
    <div>
    <form className="d-flex" role="search" onChange={handleSearch}>
        <input className="form-control me-2" type="search" placeholder="Search a movie"/>
        <i className="bi bi-search-heart"></i>
      </form>
    </div>
<div className='d-flex justify-content'className="btn-group" role="group">
{genres.map((genre)=>
  <label className="btn btn-outline-secondary" for="btncheck1"  key={genre.id}>
     {genre.name} 
  <input type="checkbox" className="btn-check" id="btncheck1" autoComplete="off"></input></label>
)}
    </div>
    <Content searched={searchMovie}/>
    </>
  )
}

export default Header
