import { createContext, useState } from 'react';

const MoviesInitialValues = {
  id: null,
  findGenre: false,
  toggleGenre: () => null
};

export const GenreContext = createContext(MoviesInitialValues);

export const GenreContextProvider = ({children}) => {
    const [genre, setGenre] = useState( { 
       id: null, 
       findGenre: false
       })

      const toggleGenre = (id) =>{}
      if(!id) return
       setGenre({
         id: id,
        findGenre: !genre.findGenre,
       });


     return  <GenreContext.Provider value={{...genre, toggleGenre}} >{children}</GenreContext.Provider>
   }
