import axios from 'axios'
import React, {useState, useEffect } from 'react'
import Navbar from './components/Navbar'
import Grid from './components/Grid'
import Content from './components/Content'


const App = () => {
const [popularMovies, setPopularMovies] = useState([])
const [genres, setGenres] = useState([])

/* const [popTvShows, setPopTvShows] = useState([])
const [movieDetails, setMovieDetails] = useState('') */
useEffect(()=>{
  axios.get('/api/movie/popular')
  .then(res =>  res.data)
  .then(pop=> setPopularMovies(pop.results))

 /*  axios.get('api/movie/details/:6')//VEER
  .then(res => console.log(res.data)) */
  axios.get('https://api.themoviedb.org/3/genre/movie/list?api_key=3e9bf10c4b9da58346377bed9363e958&language=es-MX')
  .then((res)=> res.data.results)
},[])

  return (
    <>
    <Navbar/>
    <Grid popMovies={popularMovies}/>
    <Content genres={genres}/>
    </>
  )
}

export default App
