import { createContext, useState } from 'react';

const SignInitialValues = {
    user: null, 
    isSigned: false,
    toggleSign: () => null
};

export const SignContext = createContext(SignInitialValues);

export const SignContextProvider = ({children}) => {
    const [isSignedIn, setIsSignedIn] = useState( { 
       user: null, 
       isSigned: false, 
       })

      const toggleSign = (user) =>
       setIsSignedIn({
         user: user,
         isSigned: !isSignedIn.isSigned,
       });


     return  <SignContext.Provider value={{...isSignedIn, toggleSign}} >{children}</SignContext.Provider>
   }
