import { useContext } from "react";
import { DetailContext } from "../context/Details";

const img = "https://image.tmdb.org/t/p/w500";

const Card = ({ movie }) => {
  const details = useContext(DetailContext);
  console.log(details);

  return (
      <div className="card">
        <img
          src={`${img}${movie.poster_path}`}
          className="card-img-top"
          alt="img"
          onClick={() => {
            details.toggleView(movie.id, movie.overview);
          }}
        />
        <div className="card-text text-center">
          <p>{movie.title}</p>
        </div>
        <div>
        {movie.overview}
        <i className="bi bi-plus-square btn"></i>
        </div>
      </div>

  );
};
export default Card;
