import axios from 'axios'
import React, {useState, useEffect } from 'react'
import Navbar from './components/Navbar'
import Grid from './components/Grid'
//import Content from './components/Conten


const App = () => {
const [popularMovies, setPopularMovies] = useState([])
const [searchMovie, setSearchMovie] = useState([])

useEffect(()=>{
  axios.get('/api/movie/popular')
  .then(res =>  res.data)
  .then(pop=> setPopularMovies(pop.results))

  axios.get("https://api.themoviedb.org/3/search/movie?api_key=3e9bf10c4b9da58346377bed9363e958&query=Jack+Reacher")
  .then(movie => movie.data)
  .then()
},[])

  return (
    <>
    <Navbar/>
    <Grid popMovies={popularMovies}/>
    </>
  )
}

export default App
