import axios from 'axios';
import React, { useState } from 'react'
import Movies from '../pages/Movies';
import Content from './Content';


const Header = ({genres}) => {
  console.log(genres);
  const [query, setQuery] = useState("")
  const [searchedMovies, setSearchedMovies] = useState([])

 const handleSearch=(e)=>{
  e.preventDefault();
  setQuery(e.target.value)
  axios.get('/api/movie/search', query)
  .then(res=> setSearchedMovies(res.data.results))
 } 
  return (
<>
    <div>
    <form className="d-flex" role="search">
        <input value={query} className="form-control me-2" type="search" placeholder="Search a movie" onChange={handleSearch}/>
        <i className="bi bi-search-heart"></i>
      </form>
    </div>
<div className="container-fluid d-flex btn-group" role="group">
{genres.map((genre)=>
  <label className="btn btn-outline-secondary" htmlFor="btncheck1"  key={genre.id}>
     {genre.name} 
  <input type="checkbox" className="btn-check" id="btncheck1" autoComplete="off"></input></label>
)}
    </div>
    <Content searched={searchedMovies}/> 
    <Movies genres={genres}/>
    </>
  )
}

export default Header
