import React from 'react'
const img = "https://image.tmdb.org/t/p/w500";

const Content = ({searched, genres}) => {
  return(
    <> 
    <div className='d-flex'class="btn-group" role="group">
{genres.map((genre)=>
  <label class="btn btn-outline-secondary" for="btncheck1"  key={genre.id}>
     {genre.name} 
  <input type="checkbox" class="btn-check" id="btncheck1" autocomplete="off"></input></label>
)}
    </div>
  <div>
{searched.map((movies)=>
  <img src={`${img}${movies.poster_path}`} alt='movies'/>
)}
  </div>
  </>
  )
}


export default Content
