import axios from "axios";
import React, { useContext } from "react";
import { GenreContext } from "../context/MoviesGenres";
const img = "https://image.tmdb.org/t/p/w500"


const Movies = ({ genres, top }) => {
 const genreContext= useContext(GenreContext)
  console.log(genres)

  const findSimilar=(e)=>{
    const{id} = genreContext
    e.preventDefault();
    genreContext.toggleGenre(id);
    axios.post(`/api/movie/bygenre/${id}`).then((res) => { console.log(res) })
  }
  const goback=()=>{
    return genreContext.toggleGenre()
  }
  return (
    <>
      {genres.map((genre) => (
        <div className="btn" onClick={findSimilar} key={genre.id}>
          {genre.name}
        </div>
      ))}
<div className='container-fluid row align-items-center'>
  {top.map((topmovie)=>
  <div key={topmovie.id} className="card col-md-3 d-flex container-fluid">
    <img src={`${img}${topmovie.poster_path}`}/>
    <p className="card-text">{topmovie.title}</p>
  </div>
  )}
</div>
      </>
  );
};

export default Movies;
