import axios from "axios";
import React, { useContext } from "react";
import { GenreContext } from "../context/MoviesGenres";
const img = "https://image.tmdb.org/t/p/w500";

const Movies = ({ genres, top }) => {
  const genreContext = useContext(GenreContext);
  console.log(genres);

  const findSimilar = (e) => {
    console.log(id);
    const { id } = genreContext;
    genreContext.toggleGenre(id);
    axios.get(`/api/movie/bygenre/${id}`).then((res) => {
      console.log(res);
    });
  };

  console.log(genreContext);
  return (
    <>
      {genres.map((genre) => (
        <div className="btn" onClick={(e) => findSimilar(genre.id)}  key={genre.id}>
          {genre.name}
        </div>
      ))}

      <div className="container-fluid row align-items-center">
        {top.map((topmovie) => (
          <div
            key={topmovie.id}
            className="card col-md-3 d-flex container-fluid"
          >
            <img src={`${img}${topmovie.poster_path}`} />
            <p className="card-text">{topmovie.title}</p>
          </div>
        ))}
      </div>
    </>
  );
};

export default Movies;
